// frontend/src/components/Sidebar.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllBrands } from '../services/brandService';
import { getAllNotes } from '../services/noteService';

const Sidebar = () => {
  const [brands, setBrands] = useState([]);
  const [notes, setNotes] = useState([]);
  const [brandFilter, setBrandFilter] = useState('');
  const [scentFilter, setScentFilter] = useState('');
  const [genderFilter, setGenderFilter] = useState('');
  const [sortOption, setSortOption] = useState('name-asc');
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const brandsData = await getAllBrands();
        const notesData = await getAllNotes();
        setBrands(brandsData);
        setNotes(notesData);
      } catch (error) {
        console.error('Hiba az adatok betöltésénél:', error);
      }
    };
    fetchData();
  }, []);

  const handleSearch = () => {
    const queryParams = new URLSearchParams();
    if (brandFilter) queryParams.set('brand', brandFilter);
    if (scentFilter) queryParams.set('scent', scentFilter);
    if (genderFilter) queryParams.set('gender', genderFilter);
    if (sortOption) queryParams.set('sort', sortOption);

    navigate(`/kereses?${queryParams.toString()}`);
    setIsOpen(false);
  };

  return (
    <div className="sidebar bg-light">
      <div className="mb-3">
        <label htmlFor="brand" className="form-label">
          Márka
        </label>
        <select
          id="brand"
          className="form-select"
          value={brandFilter}
          onChange={(e) => setBrandFilter(e.target.value)}
        >
          <option value="">Összes</option>
          {brands.map((brand) => (
            <option key={brand.id} value={brand.name}>
              {brand.name}
            </option>
          ))}
        </select>
      </div>

      <div className="mb-3">
        <label htmlFor="scent" className="form-label">
          Illatjegy
        </label>
        <select
          id="scent"
          className="form-select"
          value={scentFilter}
          onChange={(e) => setScentFilter(e.target.value)}
        >
          <option value="">Összes</option>
          {notes.map((note) => (
            <option key={note.id} value={note.name}>
              {note.name}
            </option>
          ))}
        </select>
      </div>

      <div className="mb-3">
        <label htmlFor="gender" className="form-label">
          Nem
        </label>
        <select
          id="gender"
          className="form-select"
          value={genderFilter}
          onChange={(e) => setGenderFilter(e.target.value)}
        >
          <option value="">Összes</option>
          <option value="male">Férfi</option>
          <option value="female">Női</option>
          <option value="unisex">Unisex</option>
        </select>
      </div>

      <button className="btn btn-primary w-100" onClick={handleSearch}>
        Keresés
      </button>
    </div>
  );
};

export default Sidebar;
